#!/usr/bin/env python3
import sys


def solve(N: int, K: int, A: "List[int]", B: "List[int]"):

    a_list = [(i + 1, A[i]) for i in range(N)]

    a_max = max(A)
    a_list = sorted(a_list, reverse=True, key=lambda x: x[1])

    idx = 0
    while a_list[idx][1] == a_max:

        if a_list[idx][0] in B:
            print('Yes')
            return

        idx += 1

    print('No')



# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    K = int(next(tokens))  # type: int
    A = [int(next(tokens)) for _ in range(N)]  # type: "List[int]"
    B = [int(next(tokens)) for _ in range(K)]  # type: "List[int]"
    solve(N, K, A, B)

if __name__ == '__main__':
    main()
