#!/usr/bin/env python3


def prime_factorize(n):

    fact_list = []
    while n % 2 == 0:
        fact_list.append(2)
        n //= 2
    f = 3
    while f * f <= n:
        if n % f == 0:
            fact_list.append(f)
            n //= f
        else:
            f += 2
    if n != 1:
        fact_list.append(n)
    return fact_list


# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    N = int(input())
    test = [int(input()) for _ in range(N)]

    for t in test:
        primes = prime_factorize(t)

        prime_set = list(set(primes))

        p, q = prime_set
        if primes.count(prime_set[0]) == 1:
            p, q = q, p

        print(p, q)



if __name__ == '__main__':
    main()
