#!/usr/bin/env python3


# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    H, W = map(int, input().split())

    C = [input() for _ in range(H)]

    c_pos = []

    for i in range(1, H - 1):
        for j in range(1, W - 1):

            if C[i][j] != "#":
                continue

            check_rel_pos = [(-1, -1), (1, -1), (0, 0), (-1, 1), (1, 1)]
            uncheck_rel_pos = [(0, -1), (-1, 0), (0, 1), (1, 0)]

            is_ok = True
            for fi, fj in check_rel_pos:
                if C[i + fi][j + fj] != "#":
                    is_ok = False
                    break
            for fi, fj in uncheck_rel_pos:
                if C[i + fi][j + fj] != ".":
                    is_ok = False
                    break

            if is_ok:
                c_pos.append((i, j))

    ans_list = [0] * min(W, H)

    for ci, cj in c_pos:
        ni, nj = ci - 1, cj - 1
        cnt = -1
        while True:
            if ni < 0 or nj < 0 or C[ni][nj] != "#":
                break

            ni -= 1
            nj -= 1
            cnt += 1

        ans_list[cnt] += 1

    print(*ans_list)


if __name__ == '__main__':
    main()
