#!/usr/bin/env python3


# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    N = int(input())
    C = []
    A = []
    for _ in range(N):
        C.append(int(input()))
        A.append(list(map(int, input().split())))
    X = int(input())

    stock = []
    min_c = 100
    for i in range(N):
        if X in A[i]:
            stock.append((C[i], i + 1))
            min_c = min(C[i], min_c)

    ans_list = []
    for c, i in stock:
        if c == min_c:
            ans_list.append(i)

    ans_list.sort()
    print(len(ans_list))
    print(*ans_list)

if __name__ == '__main__':
    main()
