#!/usr/bin/env python3
from collections import deque

# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    H, W = map(int, input().split())

    S = [input() for _ in range(H)]

    used = [[False] * W for _ in range(H)]

    def search(i, j):
        nonlocal used

        if S[i][j] != "#":
            return False

        que = deque()

        que.append((i, j))
        used[i][j] = True

        while que:
            ci, cj = que.popleft()

            for di in range(-1, 2):
                for dj in range(-1, 2):
                    if di == 0 and dj == 0:
                        continue
                    ni, nj = ci + di, cj + dj
                    if ni < 0 or nj < 0 or ni >= H or nj >= W:
                        continue

                    if not used[ni][nj] and S[ni][nj] == "#":
                        que.append((ni, nj))
                        used[ni][nj] = True
        return True


    ans = 0
    for i in range(H):
        for j in range(W):
            if used[i][j]:
                continue

            if search(i, j):
                ans += 1

    print(ans)

if __name__ == '__main__':
    main()
