#!/usr/bin/env python3
import sys


def solve(N: int):

    c = 2
    ans = 1
    while True:
        k = c ** 3
        if k > N:
            break

        k_str = str(k)
        n_k = len(k_str)
        is_ok = True
        for j in range(n_k):
            head, tail = j, n_k - j - 1
            if k_str[head] != k_str[tail]:
                is_ok = False
                break
            if head >= tail:
                break
        if is_ok:
            ans = max(ans, k)

        c += 1

    print(ans)


# Generated by 2.13.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    solve(N)

if __name__ == '__main__':
    main()
