#!/usr/bin/env python3
import sys
from atcoder.fenwicktree import FenwickTree


def solve(N: int, S: str):
    
    def create_cumsum():
        encode_map = {"A": 1, "B": -1, "C": 0}
        encode = [encode_map[c] for c in S]
        
        cumsum = [0] * (N + 1)
        for idx, e in enumerate(encode):
            cumsum[idx + 1] = cumsum[idx] + e
        return cumsum

    cumsum = create_cumsum()
    bit = FenwickTree(2*N + 1)

    ans = 0
    for num in cumsum:
       bit.add(num + N, 1)
       ans += bit.sum(0, num + N)
    
    print(ans)


# Generated by 2.16.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    S = next(tokens)  # type: str
    solve(N, S)

if __name__ == '__main__':
    main()
