#!/usr/bin/env python3
import sys
from atcoder import dsu
from atcoder import segtree
from bisect import bisect_left
from collections import defaultdict
from collections import deque
# import pypyjit
# pypyjit.set_param(inlining=0)     # or pypyjit.set_param('max_unroll_recursion=-1')

sys.setrecursionlimit(10 ** 6)

INF = 2 ** 64 - 1


# Generated by 2.16.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    N, Q = map(int, input().split())
    A = list(map(int, input().split()))
    QUERY = [list(map(int, input().split())) for _ in range(Q)]

    op = lambda x, y: x + y
    e = 0

    tree = segtree.SegTree(op, e, A)

    for query in QUERY:
        if query[0] == 1:
            x = query[1] - 1
            vx0 = tree.get(x)
            vx1 = tree.get(x + 1)
            tree.set(x, vx0 + (vx1 - vx0))
            tree.set(x + 1, vx1 + (vx0 - vx1))
        else:
            l, r = query[1] - 1, query[2]
            ans = tree.prod(l, r)
            print(ans)


if __name__ == '__main__':
    main()
