#!/usr/bin/env python3
import sys

"""
    0 1 2 ... W
0 |
1 |
2 |
: |
N |
"""


def solve(N: int, W: int, w_list: "List[int]", v_list: "List[int]"):

    dp = [[0] * (W + 1) for _ in range(N + 1)]

    for i in range(N):
        for w in range(W + 1):
            if w - w_list[i] >= 0:
                dp[i + 1][w] = max(dp[i][w], dp[i][w - w_list[i]] + v_list[i])
            else:
                dp[i + 1][w] = dp[i][w]

    print(dp[-1][-1])


# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    W = int(next(tokens))  # type: int
    w_list = [int()] * (N)  # type: "List[int]"
    v_list = [int()] * (N)  # type: "List[int]"
    for i in range(N):
        w_list[i] = int(next(tokens))
        v_list[i] = int(next(tokens))
    solve(N, W, w_list, v_list)

if __name__ == '__main__':
    main()
